mapper.update:
UPDATE `system_user` SET
OPERATER_STATUS = #{operaterStatus,jdbcType=INTEGER},
OPERATER_ID = #{operaterId,jdbcType=BIGINT},
OPERATER_TIME = #{operaterTime}
WHERE ID IN

    #{item}

mapper.select:
select , cuser.LOGIN_NAME createUserName, ouser.LOGIN_NAME operaterUserName from `system_user` p LEFT JOIN `system_user` cuser on p.CREATE_ID = cuser.ID and cuser.OPERATER_STATUS > -1 LEFT JOIN `system_user` ouser on p.OPERATER_ID = ouser.ID and ouser.OPERATER_STATUS > -1 where p.ID = #{id,jdbcType=BIGINT} and p.OPERATER_STATUS > -1
and p.USER_LEVEL > #{gtNum,jdbcType=INTEGER}
AND p.USER_LEVEL >= #{gtaeNum,jdbcType=INTEGER}

select COUNT(p.Id)
from `system_user` p
where 1=1

    and p.LOGIN_NAME = #{loginName}


    and p.ID != #{id}

select
, cuser.LOGIN_NAME createUserName, ouser.LOGIN_NAME operaterUserName
from `system_user` p
LEFT JOIN `system_user` cuser on p.CREATE_ID = cuser.ID and cuser.OPERATER_STATUS > -1
LEFT JOIN `system_user` ouser on p.OPERATER_ID = ouser.ID and ouser.OPERATER_STATUS > -1





    order by ${sort}
    ${order}

limit #{offset}, #{limit}
